import React,{useState} from "react";

function App() {
  const [kdrama,setKdrama]=useState(["My Demon"]);
  const [drama,setDrama]=useState("");
  function inputed(e){
    setDrama(e.target.value);
  }
  function clicked(){
    document.getElementBy('myInput').value = '';
      setKdrama((prev)=>[
        ...prev,
        drama
      ]);
  }
  return (
    <div className="container">
      <div className="heading">
        <h1>To_Watch List</h1>
      </div>
      <div className="form">
        <input type="text" onChange={inputed} name="drma" />
        <button onClick={clicked}>
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {kdrama.map((dr)=>(<li>{dr}</li>))}
        </ul>
      </div>
    </div>
  );
}

export default App;
