import React,{useState} from "react";

function ToWatch(drama){
    const [click,setClick]=useState(false);
    return <li onClick={clicked} style={click&& {textDecoration:"line-through"}} >{drama.name}</li>;
    function clicked(){
        setClick(true);
    }
}
export default ToWatch;
